@section('script')
    {!! JsValidator::formRequest('Modules\Manager\Http\Requests\DownloadRequest', '#download-form') !!}
    <script src="{{asset('static/manager/js/jquery.datetimepicker.full.min.js')}}"></script>
    <script src="{{asset('static/manager/js/moment.min.js')}}"></script>
    <script>
        var brands = @json($data['brands']);
        $('#brand_id').on('change', function(){
            let brand = brands.find(item => {
                if (item.id == $(this).val()) return true;
                return false;
            });
            $('#team_id').html('');
            brand.teams.forEach(element => {
                if (element.id == 1){
                    $('#team_id').append(`<option selected value="${element.id}">${element.name}</option>`)
                } else{
                    $('#team_id').append(`<option value="${element.id}">${element.name}</option>`)

                }
            });
            $.datetimepicker.setLocale('ja');
            let objDate = function (min, max){
                return {
                    format: 'Y-m-d',
                    timepicker: false,
                    minDate: min,
                    maxDate: max,
                    scrollInput: false,
                    scrollMonth : false

                }
            }
            let date = new Date();
            let minDate = moment(brand.teams[0].created_at).toDate();
            $("#start-date").datetimepicker(objDate(minDate, date));
            $("#end-date").datetimepicker(objDate(minDate, date));
            $("#start-date").click(function (){
                $(this).datetimepicker('show');
            });
            $("#end-date").click(function (){
                $(this).datetimepicker('show');
            });
            $("#end-date").change(function (){
               let newMaxDate = moment($(this).val()).toDate();
               $("#start-date").datetimepicker(objDate(minDate, newMaxDate));
            });
            $("#start-date").change(function (){
                let newMinDate = moment($(this).val()).toDate();
                $("#end-date").datetimepicker(objDate(newMinDate, date));
            });
            $("#team_id").change(function (){
                let minDate = moment(brand.teams[parseInt($(this).val()) - 1].created_at).toDate();
                $("#start-date").val('');
                $("#end-date").val('');
                $("#start-date").datetimepicker(objDate(minDate, date));
                $("#end-date").datetimepicker(objDate(minDate, date));
            });
        })

    </script>
@endsection